(window.webpackJsonp=window.webpackJsonp||[]).push([[66],{476:function(n,e,t){"use strict";t.r(e),e.default="const {Scene} = spritejs;\nconst container = document.getElementById('container');\nconst scene = new Scene({\n  container,\n});\nconst layer = scene.layer3d('fglayer', {\n  alpha: false,\n  camera: {\n    fov: 35,\n  },\n});\nlayer.camera.attributes.pos = [0, 0, 3500 / Math.min(layer.width, layer.height)];\nlayer.camera.lookAt([0, 0, 0]);\nlayer.gl.clearColor(0, 0, 0, 1);\n\nconst {createGlobe,\n  launchMissile,\n  addMarker} = spritejs.globe;\n\nconst texture = layer.createTexture('https://img.alicdn.com/tfs/TB1mR3bX3nH8KJjSspcXXb3QFXa-9000-4500.jpg');\n\nconst globe = createGlobe(layer, {\n  orbit: true,\n  raycast: true,\n  rotateY: 310,\n  texture,\n  tick() {\n    this.attributes.rotateY += 0.1;\n  },\n  highlight: true,\n});\n\nfunction randomColor() {\n  const hue = Math.floor(Math.random() * 360);\n  return `hsl(${hue}, 100%, 50%)`;\n}\n\nsetInterval(() => {\n  const from = {\n    latitude: 90 - 180 * Math.random(),\n    longitude: 180 - 360 * Math.random(),\n  };\n  const to = {\n    latitude: 90 - 180 * Math.random(),\n    longitude: 180 - 360 * Math.random(),\n  };\n  const color = randomColor();\n  const lifeTime = 1000;\n  const height = 2.0;\n  const width = 1.0;\n\n  addMarker(globe, {\n    ...from,\n    lifeTime,\n    height,\n    color,\n    width,\n    // speed: 0.5,\n    // segments: 4,\n  });\n\n  setTimeout(() => {\n    addMarker(globe, {\n      ...to,\n      lifeTime,\n      height,\n      color,\n      width,\n      // speed: 0.5,\n      segments: 4,\n    });\n  }, 0.5 * lifeTime);\n\n  launchMissile(globe, {\n    from,\n    to,\n    color,\n    lifeTime,\n    height,\n    thickness: 0.75 * width,\n  });\n}, 100);\n"}}]);